import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { 
  Plus, 
  Search, 
  Download, 
  Upload,
  Users,
  UserCheck,
  UserMinus,
  Edit,
  Trash,
  Mail,
  Tag
} from "lucide-react";

interface Contact {
  id: string;
  email: string;
  firstName?: string;
  lastName?: string;
  status: 'active' | 'unsubscribed' | 'bounced' | 'complained';
  subscriptionDate: string;
  lastEngagementDate?: string;
  tags?: string[];
  customFields?: Record<string, any>;
  createdAt: string;
}

interface ContactList {
  id: string;
  name: string;
  description?: string;
  createdAt: string;
}

interface ContactsResponse {
  contacts: Contact[];
  total: number;
}

export default function Contacts() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState("all");
  const [selectedList, setSelectedList] = useState("all");
  const [selectedContacts, setSelectedContacts] = useState<string[]>([]);
  const [showAddContact, setShowAddContact] = useState(false);
  const [showAddList, setShowAddList] = useState(false);
  const [showImport, setShowImport] = useState(false);

  const [newContact, setNewContact] = useState({
    email: "",
    firstName: "",
    lastName: "",
    tags: "",
  });

  const [newList, setNewList] = useState({
    name: "",
    description: "",
  });

  // Fetch contacts
  const { data: contactsData, isLoading: contactsLoading } = useQuery<ContactsResponse>({
    queryKey: ['/api/contacts', { listId: selectedList !== 'all' ? selectedList : undefined }],
  });

  // Fetch contact lists
  const { data: contactLists, isLoading: listsLoading } = useQuery<ContactList[]>({
    queryKey: ['/api/contact-lists'],
  });

  // Create contact mutation
  const createContactMutation = useMutation({
    mutationFn: async (contactData: any) => {
      const response = await apiRequest('POST', '/api/contacts', contactData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/contacts'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      setShowAddContact(false);
      setNewContact({ email: "", firstName: "", lastName: "", tags: "" });
      toast({
        title: "Contact added successfully",
        description: "The contact has been added to your list.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error adding contact",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  // Create contact list mutation
  const createListMutation = useMutation({
    mutationFn: async (listData: any) => {
      const response = await apiRequest('POST', '/api/contact-lists', listData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/contact-lists'] });
      setShowAddList(false);
      setNewList({ name: "", description: "" });
      toast({
        title: "Contact list created successfully",
        description: "Your new contact list is ready to use.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error creating list",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  const handleCreateContact = () => {
    if (!newContact.email) {
      toast({
        title: "Email required",
        description: "Please enter a valid email address.",
        variant: "destructive",
      });
      return;
    }

    const contactData = {
      ...newContact,
      tags: newContact.tags ? newContact.tags.split(',').map(tag => tag.trim()).filter(Boolean) : [],
    };

    createContactMutation.mutate(contactData);
  };

  const handleCreateList = () => {
    if (!newList.name) {
      toast({
        title: "List name required",
        description: "Please enter a name for the contact list.",
        variant: "destructive",
      });
      return;
    }

    createListMutation.mutate(newList);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'unsubscribed': return 'bg-red-100 text-red-800';
      case 'bounced': return 'bg-orange-100 text-orange-800';
      case 'complained': return 'bg-yellow-100 text-yellow-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const filteredContacts = contactsData?.contacts?.filter(contact => {
    const matchesSearch = contact.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         contact.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         contact.lastName?.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesStatus = statusFilter === 'all' || contact.status === statusFilter;
    return matchesSearch && matchesStatus;
  }) || [];

  const handleSelectContact = (contactId: string, checked: boolean) => {
    if (checked) {
      setSelectedContacts(prev => [...prev, contactId]);
    } else {
      setSelectedContacts(prev => prev.filter(id => id !== contactId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedContacts(filteredContacts.map(c => c.id));
    } else {
      setSelectedContacts([]);
    }
  };

  if (contactsLoading || listsLoading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-slate-900">Contacts</h1>
            <p className="text-slate-600 mt-1">Manage your subscriber lists and contacts.</p>
          </div>
        </div>
        <div className="animate-pulse space-y-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-20 bg-slate-200 rounded"></div>
          ))}
        </div>
      </div>
    );
  }

  const totalContacts = contactsData?.total || 0;
  const activeContacts = filteredContacts.filter(c => c.status === 'active').length;
  const unsubscribedContacts = filteredContacts.filter(c => c.status === 'unsubscribed').length;

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-slate-900">Contacts</h1>
          <p className="text-slate-600 mt-1">Manage your subscriber lists and contacts.</p>
        </div>
        <div className="flex space-x-3">
          <Button variant="outline" className="flex items-center gap-2">
            <Download className="h-4 w-4" />
            Export
          </Button>
          <Dialog open={showImport} onOpenChange={setShowImport}>
            <DialogTrigger asChild>
              <Button variant="outline" className="flex items-center gap-2">
                <Upload className="h-4 w-4" />
                Import
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Import Contacts</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label>CSV File</Label>
                  <Input type="file" accept=".csv" className="mt-1" />
                  <p className="text-sm text-slate-500 mt-1">
                    Upload a CSV file with columns: email, firstName, lastName, tags
                  </p>
                </div>
                <Button className="w-full">Import Contacts</Button>
              </div>
            </DialogContent>
          </Dialog>
          <Dialog open={showAddList} onOpenChange={setShowAddList}>
            <DialogTrigger asChild>
              <Button variant="outline" className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                New List
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Create Contact List</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="listName">List Name*</Label>
                  <Input
                    id="listName"
                    value={newList.name}
                    onChange={(e) => setNewList(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="Enter list name"
                  />
                </div>
                <div>
                  <Label htmlFor="listDescription">Description</Label>
                  <Textarea
                    id="listDescription"
                    value={newList.description}
                    onChange={(e) => setNewList(prev => ({ ...prev, description: e.target.value }))}
                    placeholder="Optional description"
                  />
                </div>
                <Button onClick={handleCreateList} disabled={createListMutation.isPending} className="w-full">
                  {createListMutation.isPending ? 'Creating...' : 'Create List'}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
          <Dialog open={showAddContact} onOpenChange={setShowAddContact}>
            <DialogTrigger asChild>
              <Button className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                Add Contact
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Add Contact</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div>
                  <Label htmlFor="email">Email Address*</Label>
                  <Input
                    id="email"
                    type="email"
                    value={newContact.email}
                    onChange={(e) => setNewContact(prev => ({ ...prev, email: e.target.value }))}
                    placeholder="contact@example.com"
                  />
                </div>
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="firstName">First Name</Label>
                    <Input
                      id="firstName"
                      value={newContact.firstName}
                      onChange={(e) => setNewContact(prev => ({ ...prev, firstName: e.target.value }))}
                      placeholder="John"
                    />
                  </div>
                  <div>
                    <Label htmlFor="lastName">Last Name</Label>
                    <Input
                      id="lastName"
                      value={newContact.lastName}
                      onChange={(e) => setNewContact(prev => ({ ...prev, lastName: e.target.value }))}
                      placeholder="Doe"
                    />
                  </div>
                </div>
                <div>
                  <Label htmlFor="tags">Tags (comma-separated)</Label>
                  <Input
                    id="tags"
                    value={newContact.tags}
                    onChange={(e) => setNewContact(prev => ({ ...prev, tags: e.target.value }))}
                    placeholder="customer, premium, newsletter"
                  />
                </div>
                <Button onClick={handleCreateContact} disabled={createContactMutation.isPending} className="w-full">
                  {createContactMutation.isPending ? 'Adding...' : 'Add Contact'}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-lg font-semibold text-slate-900">Total Contacts</h3>
              <Users className="h-5 w-5 text-primary" />
            </div>
            <p className="text-3xl font-bold text-slate-900">{totalContacts.toLocaleString()}</p>
            <p className="text-sm text-slate-500 mt-1">All contacts in your database</p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-lg font-semibold text-slate-900">Active Subscribers</h3>
              <UserCheck className="h-5 w-5 text-green-600" />
            </div>
            <p className="text-3xl font-bold text-slate-900">{activeContacts.toLocaleString()}</p>
            <p className="text-sm text-slate-500 mt-1">
              {totalContacts > 0 ? Math.round((activeContacts / totalContacts) * 100) : 0}% of total
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between mb-3">
              <h3 className="text-lg font-semibold text-slate-900">Unsubscribed</h3>
              <UserMinus className="h-5 w-5 text-red-600" />
            </div>
            <p className="text-3xl font-bold text-slate-900">{unsubscribedContacts.toLocaleString()}</p>
            <p className="text-sm text-slate-500 mt-1">
              {totalContacts > 0 ? Math.round((unsubscribedContacts / totalContacts) * 100) : 0}% of total
            </p>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <div className="flex items-center gap-4">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-slate-400" />
          <Input
            placeholder="Search contacts..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
          />
        </div>
        <Select value={selectedList} onValueChange={setSelectedList}>
          <SelectTrigger className="w-48">
            <SelectValue placeholder="All Lists" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Lists</SelectItem>
            {contactLists?.map((list) => (
              <SelectItem key={list.id} value={list.id}>
                {list.name}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
        <Select value={statusFilter} onValueChange={setStatusFilter}>
          <SelectTrigger className="w-40">
            <SelectValue placeholder="All Status" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Status</SelectItem>
            <SelectItem value="active">Active</SelectItem>
            <SelectItem value="unsubscribed">Unsubscribed</SelectItem>
            <SelectItem value="bounced">Bounced</SelectItem>
            <SelectItem value="complained">Complained</SelectItem>
          </SelectContent>
        </Select>
      </div>

      {/* Bulk Actions */}
      {selectedContacts.length > 0 && (
        <Card className="border-primary">
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">
                {selectedContacts.length} contact{selectedContacts.length !== 1 ? 's' : ''} selected
              </span>
              <div className="flex items-center gap-2">
                <Button variant="outline" size="sm">
                  Add to List
                </Button>
                <Button variant="outline" size="sm">
                  Export Selected
                </Button>
                <Button variant="destructive" size="sm">
                  Delete Selected
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Contacts Table */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>All Contacts</CardTitle>
            <span className="text-sm text-slate-500">
              {filteredContacts.length} of {totalContacts} contacts
            </span>
          </div>
        </CardHeader>
        <CardContent>
          {filteredContacts.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4">
                      <Checkbox
                        checked={selectedContacts.length === filteredContacts.length && filteredContacts.length > 0}
                        onCheckedChange={handleSelectAll}
                      />
                    </th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Contact</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Status</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Tags</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Subscribed</th>
                    <th className="text-right py-3 px-4 font-medium text-slate-500">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredContacts.map((contact) => (
                    <tr key={contact.id} className="border-b hover:bg-slate-50">
                      <td className="py-3 px-4">
                        <Checkbox
                          checked={selectedContacts.includes(contact.id)}
                          onCheckedChange={(checked) => handleSelectContact(contact.id, checked as boolean)}
                        />
                      </td>
                      <td className="py-3 px-4">
                        <div className="flex items-center gap-3">
                          <div className="w-8 h-8 bg-slate-200 rounded-full flex items-center justify-center">
                            <Mail className="h-4 w-4 text-slate-600" />
                          </div>
                          <div>
                            <div className="font-medium text-slate-900">
                              {contact.firstName || contact.lastName 
                                ? `${contact.firstName || ''} ${contact.lastName || ''}`.trim()
                                : contact.email.split('@')[0]}
                            </div>
                            <div className="text-sm text-slate-500">{contact.email}</div>
                          </div>
                        </div>
                      </td>
                      <td className="py-3 px-4">
                        <Badge className={getStatusColor(contact.status)}>
                          {contact.status}
                        </Badge>
                      </td>
                      <td className="py-3 px-4">
                        <div className="flex flex-wrap gap-1">
                          {contact.tags?.slice(0, 2).map((tag, index) => (
                            <span key={index} className="px-2 py-1 text-xs bg-slate-100 text-slate-700 rounded-full flex items-center gap-1">
                              <Tag className="h-3 w-3" />
                              {tag}
                            </span>
                          ))}
                          {contact.tags && contact.tags.length > 2 && (
                            <span className="px-2 py-1 text-xs bg-slate-100 text-slate-700 rounded-full">
                              +{contact.tags.length - 2}
                            </span>
                          )}
                        </div>
                      </td>
                      <td className="py-3 px-4 text-sm text-slate-500">
                        {new Date(contact.subscriptionDate).toLocaleDateString()}
                      </td>
                      <td className="py-3 px-4 text-right">
                        <div className="flex items-center justify-end gap-2">
                          <Button variant="ghost" size="sm" className="h-8 w-8 p-0">
                            <Edit className="h-4 w-4" />
                          </Button>
                          <Button variant="ghost" size="sm" className="h-8 w-8 p-0 text-red-600">
                            <Trash className="h-4 w-4" />
                          </Button>
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-8">
              <Users className="h-12 w-12 text-slate-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-slate-900 mb-2">No contacts found</h3>
              <p className="text-slate-500 mb-4">
                {searchQuery || statusFilter !== 'all' || selectedList !== 'all'
                  ? "Try adjusting your search or filters."
                  : "Add your first contact to get started."}
              </p>
              {!searchQuery && statusFilter === 'all' && selectedList === 'all' && (
                <Button onClick={() => setShowAddContact(true)} className="flex items-center gap-2">
                  <Plus className="h-4 w-4" />
                  Add Contact
                </Button>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/CreateCampaign.tsx.content:
import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Checkbox } from "@/components/ui/checkbox";
import { RadioGroup, RadioGroupItem } from "@/components/ui/radio-group";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import EmailTemplateBuilder from "@/components/EmailTemplateBuilder";
import { 
  ArrowLeft, 
  Save, 
  Send, 
  Eye, 
  Settings,
  Users,
  Mail,
  Calendar,
  BarChart3
} from "lucide-react";

interface ContactList {
  id: string;
  name: string;
  description?: string;
}

interface SmtpServer {
  id: string;
  name: string;
  host: string;
  status: string;
}

export default function CreateCampaign() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const [campaignData, setCampaignData] = useState({
    name: "",
    subject: "",
    fromName: "",
    fromEmail: "",
    htmlContent: "",
    textContent: "",
    recipientLists: [] as string[],
    smtpServerId: "",
    scheduledAt: "",
    sendImmediately: true,
    trackOpens: true,
    trackClicks: true,
    trackUnsubscribes: false,
    trackBounces: false,
    enableAbTesting: false,
  });

  // Fetch contact lists
  const { data: contactLists } = useQuery<ContactList[]>({
    queryKey: ['/api/contact-lists'],
  });

  // Fetch SMTP servers
  const { data: smtpServers } = useQuery<SmtpServer[]>({
    queryKey: ['/api/smtp-servers'],
  });

  // Create campaign mutation
  const createCampaignMutation = useMutation({
    mutationFn: async (data: any) => {
      const response = await apiRequest('POST', '/api/campaigns', data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/campaigns'] });
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/stats'] });
      toast({
        title: "Campaign created successfully",
        description: "Your campaign has been created and is ready to send.",
      });
      setLocation('/campaigns');
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error creating campaign",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  const handleSubmit = (isDraft: boolean = false) => {
    if (!campaignData.name || !campaignData.subject || !campaignData.htmlContent) {
      toast({
        title: "Missing required fields",
        description: "Please fill in campaign name, subject, and content.",
        variant: "destructive",
      });
      return;
    }

    if (campaignData.recipientLists.length === 0) {
      toast({
        title: "No recipients selected",
        description: "Please select at least one contact list.",
        variant: "destructive",
      });
      return;
    }

    const payload = {
      ...campaignData,
      status: isDraft ? 'draft' : (campaignData.sendImmediately ? 'sending' : 'scheduled'),
      scheduledAt: campaignData.sendImmediately ? null : campaignData.scheduledAt || null,
    };

    createCampaignMutation.mutate(payload);
  };

  const handleListSelection = (listId: string, checked: boolean) => {
    if (checked) {
      setCampaignData(prev => ({
        ...prev,
        recipientLists: [...prev.recipientLists, listId]
      }));
    } else {
      setCampaignData(prev => ({
        ...prev,
        recipientLists: prev.recipientLists.filter(id => id !== listId)
      }));
    }
  };

  const handleBack = () => {
    setLocation('/campaigns');
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-4">
          <Button variant="ghost" size="sm" onClick={handleBack} className="flex items-center gap-2">
            <ArrowLeft className="h-4 w-4" />
            Back to Campaigns
          </Button>
          <div>
            <h1 className="text-2xl font-semibold text-slate-900">Create Campaign</h1>
            <p className="text-slate-600 mt-1">Set up a new email marketing campaign.</p>
          </div>
        </div>
        <div className="flex items-center gap-3">
          <Button variant="outline" onClick={() => handleSubmit(true)} disabled={createCampaignMutation.isPending}>
            <Save className="h-4 w-4 mr-2" />
            Save as Draft
          </Button>
          <Button variant="outline" className="flex items-center gap-2">
            <Eye className="h-4 w-4" />
            Preview & Test
          </Button>
          <Button onClick={() => handleSubmit(false)} disabled={createCampaignMutation.isPending}>
            <Send className="h-4 w-4 mr-2" />
            {createCampaignMutation.isPending ? 'Creating...' : 'Create Campaign'}
          </Button>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Main Content */}
        <div className="lg:col-span-2">
          <Tabs defaultValue="basic" className="space-y-6">
            <TabsList className="grid w-full grid-cols-4">
              <TabsTrigger value="basic" className="flex items-center gap-2">
                <Settings className="h-4 w-4" />
                Basic
              </TabsTrigger>
              <TabsTrigger value="recipients" className="flex items-center gap-2">
                <Users className="h-4 w-4" />
                Recipients
              </TabsTrigger>
              <TabsTrigger value="content" className="flex items-center gap-2">
                <Mail className="h-4 w-4" />
                Content
              </TabsTrigger>
              <TabsTrigger value="tracking" className="flex items-center gap-2">
                <BarChart3 className="h-4 w-4" />
                Tracking
              </TabsTrigger>
            </TabsList>

            <TabsContent value="basic">
              <Card>
                <CardHeader>
                  <CardTitle>Campaign Details</CardTitle>
                </CardHeader>
                <CardContent className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="name">Campaign Name*</Label>
                      <Input
                        id="name"
                        value={campaignData.name}
                        onChange={(e) => setCampaignData(prev => ({ ...prev, name: e.target.value }))}
                        placeholder="Enter campaign name"
                      />
                    </div>
                    <div>
                      <Label htmlFor="smtpServer">SMTP Server</Label>
                      <Select value={campaignData.smtpServerId} onValueChange={(value) => setCampaignData(prev => ({ ...prev, smtpServerId: value }))}>
                        <SelectTrigger>
                          <SelectValue placeholder="Select SMTP server" />
                        </SelectTrigger>
                        <SelectContent>
                          {smtpServers?.map((server) => (
                            <SelectItem key={server.id} value={server.id}>
                              {server.name} ({server.host})
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="subject">Subject Line*</Label>
                    <Input
                      id="subject"
                      value={campaignData.subject}
                      onChange={(e) => setCampaignData(prev => ({ ...prev, subject: e.target.value }))}
                      placeholder="Enter email subject line"
                    />
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="fromName">From Name</Label>
                      <Input
                        id="fromName"
                        value={campaignData.fromName}
                        onChange={(e) => setCampaignData(prev => ({ ...prev, fromName: e.target.value }))}
                        placeholder="Your Company"
                      />
                    </div>
                    <div>
                      <Label htmlFor="fromEmail">From Email</Label>
                      <Input
                        id="fromEmail"
                        type="email"
                        value={campaignData.fromEmail}
                        onChange={(e) => setCampaignData(prev => ({ ...prev, fromEmail: e.target.value }))}
                        placeholder="noreply@company.com"
                      />
                    </div>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="recipients">
              <Card>
                <CardHeader>
                  <CardTitle>Select Recipients</CardTitle>
                </CardHeader>
                <CardContent>
                  {contactLists && contactLists.length > 0 ? (
                    <div className="space-y-3">
                      {contactLists.map((list) => (
                        <div key={list.id} className="flex items-center space-x-2">
                          <Checkbox
                            id={list.id}
                            checked={campaignData.recipientLists.includes(list.id)}
                            onCheckedChange={(checked) => handleListSelection(list.id, checked as boolean)}
                          />
                          <label htmlFor={list.id} className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70">
                            {list.name}
                          </label>
                          {list.description && (
                            <span className="text-sm text-slate-500">- {list.description}</span>
                          )}
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="text-center py-8">
                      <Users className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                      <h3 className="text-lg font-medium text-slate-900 mb-2">No contact lists found</h3>
                      <p className="text-slate-500 mb-4">Create contact lists first to select recipients.</p>
                      <Button variant="outline" onClick={() => setLocation('/contacts')}>
                        Go to Contacts
                      </Button>
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="content">
              <Card>
                <CardHeader>
                  <CardTitle>Email Content</CardTitle>
                </CardHeader>
                <CardContent>
                  <EmailTemplateBuilder
                    value={campaignData.htmlContent}
                    onChange={(html, text) => setCampaignData(prev => ({ 
                      ...prev, 
                      htmlContent: html,
                      textContent: text || ''
                    }))}
                  />
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="tracking">
              <Card>
                <CardHeader>
                  <CardTitle>Tracking & Analytics</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="trackOpens"
                      checked={campaignData.trackOpens}
                      onCheckedChange={(checked) => setCampaignData(prev => ({ ...prev, trackOpens: checked as boolean }))}
                    />
                    <label htmlFor="trackOpens" className="text-sm font-medium">Track opens</label>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="trackClicks"
                      checked={campaignData.trackClicks}
                      onCheckedChange={(checked) => setCampaignData(prev => ({ ...prev, trackClicks: checked as boolean }))}
                    />
                    <label htmlFor="trackClicks" className="text-sm font-medium">Track clicks</label>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="trackUnsubscribes"
                      checked={campaignData.trackUnsubscribes}
                      onCheckedChange={(checked) => setCampaignData(prev => ({ ...prev, trackUnsubscribes: checked as boolean }))}
                    />
                    <label htmlFor="trackUnsubscribes" className="text-sm font-medium">Track unsubscribes</label>
                  </div>

                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="trackBounces"
                      checked={campaignData.trackBounces}
                      onCheckedChange={(checked) => setCampaignData(prev => ({ ...prev, trackBounces: checked as boolean }))}
                    />
                    <label htmlFor="trackBounces" className="text-sm font-medium">Track bounces</label>
                  </div>
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        </div>

        {/* Sidebar */}
        <div className="space-y-6">
          {/* Send Settings */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <Calendar className="h-4 w-4" />
                Send Settings
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <RadioGroup
                value={campaignData.sendImmediately ? "immediate" : "scheduled"}
                onValueChange={(value) => setCampaignData(prev => ({ ...prev, sendImmediately: value === "immediate" }))}
              >
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="immediate" id="immediate" />
                  <label htmlFor="immediate" className="text-sm">Send immediately</label>
                </div>
                <div className="flex items-center space-x-2">
                  <RadioGroupItem value="scheduled" id="scheduled" />
                  <label htmlFor="scheduled" className="text-sm">Schedule for later</label>
                </div>
              </RadioGroup>

              {!campaignData.sendImmediately && (
                <div className="mt-4">
                  <Label htmlFor="scheduledAt">Schedule Date & Time</Label>
                  <Input
                    id="scheduledAt"
                    type="datetime-local"
                    value={campaignData.scheduledAt}
                    onChange={(e) => setCampaignData(prev => ({ ...prev, scheduledAt: e.target.value }))}
                    className="mt-1"
                  />
                </div>
              )}
            </CardContent>
          </Card>

          {/* A/B Testing */}
          <Card>
            <CardHeader>
              <CardTitle>A/B Testing</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-2 mb-3">
                <Checkbox
                  id="enableAbTesting"
                  checked={campaignData.enableAbTesting}
                  onCheckedChange={(checked) => setCampaignData(prev => ({ ...prev, enableAbTesting: checked as boolean }))}
                />
                <label htmlFor="enableAbTesting" className="text-sm font-medium">Enable A/B testing</label>
              </div>
              <p className="text-sm text-slate-500">
                Test subject lines, send times, or content variations to optimize performance.
              </p>
            </CardContent>
          </Card>

          {/* Campaign Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Campaign Summary</CardTitle>
            </CardHeader>
            <CardContent className="space-y-3">
              <div className="flex justify-between text-sm">
                <span className="text-slate-600">Recipients:</span>
                <span className="font-medium">{campaignData.recipientLists.length} lists selected</span>
              </div>
              <div className="flex justify-between text-sm">
                <span className="text-slate-600">Tracking:</span>
                <span className="font-medium">
                  {[
                    campaignData.trackOpens && 'Opens',
                    campaignData.trackClicks && 'Clicks',
                    campaignData.trackUnsubscribes && 'Unsubscribes',
                    campaignData.trackBounces && 'Bounces'
                  ].filter(Boolean).join(', ') || 'None'}
                </span>
              </div>
              <div className="flex justify-between text-sm">
                <span className="text-slate-600">Send Time:</span>
                <span className="font-medium">
                  {campaignData.sendImmediately ? 'Immediately' : 'Scheduled'}
                </span>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

client/src/pages/Dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import StatsCard from "@/components/StatsCard";
import CampaignChart from "@/components/CampaignChart";
import { 
  Mail, 
  Users, 
  TrendingUp, 
  MousePointer, 
  Plus, 
  Download,
  Calendar,
  Activity,
  ArrowRight
} from "lucide-react";
import { useLocation } from "wouter";
import { format } from "date-fns";

interface DashboardStats {
  totalCampaigns: number;
  averageOpenRate: number;
  averageClickRate: number;
  totalContacts: number;
  recentCampaigns: Array<{
    id: string;
    name: string;
    status: string;
    recipientCount: number;
    openCount: number;
    clickCount: number;
    createdAt: string;
    sentAt?: string;
  }>;
}

export default function Dashboard() {
  const [, setLocation] = useLocation();

  const { data: stats, isLoading } = useQuery<DashboardStats>({
    queryKey: ['/api/dashboard/stats'],
  });

  const handleCreateCampaign = () => {
    setLocation('/campaigns/create');
  };

  const handleViewCampaigns = () => {
    setLocation('/campaigns');
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'sent': return 'bg-green-100 text-green-800';
      case 'scheduled': return 'bg-yellow-100 text-yellow-800';
      case 'draft': return 'bg-gray-100 text-gray-800';
      case 'sending': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const calculateOpenRate = (openCount: number, recipientCount: number) => {
    if (recipientCount === 0) return 0;
    return Math.round((openCount / recipientCount) * 100 * 10) / 10;
  };

  const calculateClickRate = (clickCount: number, recipientCount: number) => {
    if (recipientCount === 0) return 0;
    return Math.round((clickCount / recipientCount) * 100 * 10) / 10;
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-slate-900">Dashboard</h1>
            <p className="text-slate-600 mt-1">Welcome back! Here's what's happening with your email campaigns.</p>
          </div>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {[1, 2, 3, 4].map((i) => (
            <Card key={i} className="animate-pulse">
              <CardContent className="p-6">
                <div className="h-4 bg-slate-200 rounded w-1/2 mb-2"></div>
                <div className="h-8 bg-slate-200 rounded w-3/4 mb-2"></div>
                <div className="h-3 bg-slate-200 rounded w-full"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-slate-900">Dashboard</h1>
          <p className="text-slate-600 mt-1">Welcome back! Here's what's happening with your email campaigns.</p>
        </div>
        <div className="flex space-x-3">
          <Button variant="outline" className="flex items-center gap-2">
            <Download className="h-4 w-4" />
            Export
          </Button>
          <Button onClick={handleCreateCampaign} className="flex items-center gap-2">
            <Plus className="h-4 w-4" />
            New Campaign
          </Button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatsCard
          title="Total Campaigns"
          value={stats?.totalCampaigns || 0}
          change="+12%"
          changeType="positive"
          icon={Mail}
          iconColor="text-primary"
          iconBgColor="bg-blue-100"
        />

        <StatsCard
          title="Average Open Rate"
          value={`${stats?.averageOpenRate || 0}%`}
          change="+2.1%"
          changeType="positive"
          icon={TrendingUp}
          iconColor="text-green-600"
          iconBgColor="bg-green-100"
        />

        <StatsCard
          title="Average Click Rate"
          value={`${stats?.averageClickRate || 0}%`}
          change="-0.4%"
          changeType="negative"
          icon={MousePointer}
          iconColor="text-yellow-600"
          iconBgColor="bg-yellow-100"
        />

        <StatsCard
          title="Total Contacts"
          value={stats?.totalContacts?.toLocaleString() || '0'}
          change="+1,245"
          changeType="positive"
          icon={Users}
          iconColor="text-purple-600"
          iconBgColor="bg-purple-100"
        />
      </div>

      {/* Charts Section */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <CampaignChart />

        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Campaign Performance</CardTitle>
              <select className="text-sm border border-slate-300 rounded-lg px-3 py-1 text-slate-600">
                <option>Last 7 days</option>
                <option>Last 30 days</option>
                <option>Last 90 days</option>
              </select>
            </div>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <span className="text-sm text-slate-600">Open Rate</span>
                <div className="flex items-center gap-2">
                  <div className="w-24 bg-slate-200 rounded-full h-2">
                    <div className="bg-green-500 h-2 rounded-full" style={{ width: `${stats?.averageOpenRate || 0}%` }}></div>
                  </div>
                  <span className="text-sm font-medium">{stats?.averageOpenRate || 0}%</span>
                </div>
              </div>
              <div className="flex items-center justify-between">
                <span className="text-sm text-slate-600">Click Rate</span>
                <div className="flex items-center gap-2">
                  <div className="w-24 bg-slate-200 rounded-full h-2">
                    <div className="bg-blue-500 h-2 rounded-full" style={{ width: `${stats?.averageClickRate || 0}%` }}></div>
                  </div>
                  <span className="text-sm font-medium">{stats?.averageClickRate || 0}%</span>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Campaigns */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Recent Campaigns</CardTitle>
            <Button variant="ghost" onClick={handleViewCampaigns} className="flex items-center gap-1">
              View all campaigns
              <ArrowRight className="h-4 w-4" />
            </Button>
          </div>
        </CardHeader>
        <CardContent>
          {stats?.recentCampaigns && stats.recentCampaigns.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b">
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Campaign</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Status</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Recipients</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Open Rate</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Click Rate</th>
                    <th className="text-left py-3 px-4 font-medium text-slate-500">Date</th>
                  </tr>
                </thead>
                <tbody>
                  {stats.recentCampaigns.map((campaign) => (
                    <tr key={campaign.id} className="border-b hover:bg-slate-50">
                      <td className="py-3 px-4">
                        <div className="flex items-center gap-3">
                          <div className={`w-2 h-2 rounded-full ${campaign.status === 'sent' ? 'bg-green-500' : campaign.status === 'scheduled' ? 'bg-yellow-500' : 'bg-gray-500'}`}></div>
                          <div>
                            <div className="font-medium text-slate-900">{campaign.name}</div>
                          </div>
                        </div>
                      </td>
                      <td className="py-3 px-4">
                        <Badge className={getStatusColor(campaign.status)}>
                          {campaign.status}
                        </Badge>
                      </td>
                      <td className="py-3 px-4 text-slate-900">
                        {campaign.recipientCount.toLocaleString()}
                      </td>
                      <td className="py-3 px-4 text-slate-900">
                        {campaign.status === 'sent' ? `${calculateOpenRate(campaign.openCount, campaign.recipientCount)}%` : '-'}
                      </td>
                      <td className="py-3 px-4 text-slate-900">
                        {campaign.status === 'sent' ? `${calculateClickRate(campaign.clickCount, campaign.recipientCount)}%` : '-'}
                      </td>
                      <td className="py-3 px-4 text-slate-500">
                        {campaign.sentAt ? format(new Date(campaign.sentAt), 'MMM d, yyyy') : format(new Date(campaign.createdAt), 'MMM d, yyyy')}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-8">
              <Activity className="h-12 w-12 text-slate-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-slate-900 mb-2">No campaigns yet</h3>
              <p className="text-slate-500 mb-4">Create your first email campaign to get started.</p>
              <Button onClick={handleCreateCampaign} className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                Create Campaign
              </Button>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Deliverability.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";
import { Button } from "@/components/ui/button";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { 
  Mail, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  TrendingUp, 
  Shield, 
  Server,
  Globe,
  Download
} from "lucide-react";

interface DeliverabilityStats {
  deliveryRate: number;
  bounceRate: number;
  complaintRate: number;
  inboxPlacement: number;
  spamPlacement: number;
  reputationScore: number;
  trend: 'up' | 'down' | 'stable';
}

interface ServerHealth {
  id: string;
  name: string;
  status: 'healthy' | 'warning' | 'critical';
  deliveryRate: number;
  bounceRate: number;
  lastCheck: string;
  issues: string[];
}

interface DomainReputation {
  domain: string;
  reputation: 'excellent' | 'good' | 'fair' | 'poor';
  score: number;
  blacklisted: boolean;
  lastChecked: string;
}

export default function Deliverability() {
  const [dateRange, setDateRange] = useState("30");
  const [selectedServer, setSelectedServer] = useState("all");

  const { data: deliverabilityStats } = useQuery<DeliverabilityStats>({
    queryKey: ["/api/analytics/deliverability", dateRange, selectedServer],
  });

  const { data: serverHealth = [] } = useQuery<ServerHealth[]>({
    queryKey: ["/api/analytics/server-health"],
  });

  const { data: domainReputations = [] } = useQuery<DomainReputation[]>({
    queryKey: ["/api/analytics/domain-reputation"],
  });

  const { data: bounceAnalysis = [] } = useQuery({
    queryKey: ["/api/analytics/bounce-analysis", dateRange],
  });

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'healthy':
      case 'excellent':
      case 'good':
        return <CheckCircle className="h-5 w-5 text-green-600" />;
      case 'warning':
      case 'fair':
        return <AlertTriangle className="h-5 w-5 text-yellow-600" />;
      case 'critical':
      case 'poor':
        return <XCircle className="h-5 w-5 text-red-600" />;
      default:
        return <AlertTriangle className="h-5 w-5 text-gray-600" />;
    }
  };

  const getReputationBadge = (reputation: string) => {
    const variants = {
      excellent: "default",
      good: "secondary", 
      fair: "outline",
      poor: "destructive"
    } as const;

    return <Badge variant={variants[reputation as keyof typeof variants] || "outline"}>{reputation}</Badge>;
  };

  const stats = deliverabilityStats || {
    deliveryRate: 0,
    bounceRate: 0,
    complaintRate: 0,
    inboxPlacement: 0,
    spamPlacement: 0,
    reputationScore: 0,
    trend: 'stable' as const
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Deliverability</h1>
          <p className="text-muted-foreground mt-1">
            Monitor your email delivery performance and sender reputation.
          </p>
        </div>
        <div className="flex space-x-2">
          <Select value={dateRange} onValueChange={setDateRange}>
            <SelectTrigger className="w-36">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="7">Last 7 days</SelectItem>
              <SelectItem value="30">Last 30 days</SelectItem>
              <SelectItem value="90">Last 90 days</SelectItem>
            </SelectContent>
          </Select>
          <Button variant="outline">
            <Download className="h-4 w-4 mr-2" />
            Export Report
          </Button>
        </div>
      </div>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Delivery Rate</p>
                <p className="text-2xl font-bold">{stats.deliveryRate}%</p>
              </div>
              <CheckCircle className={`h-8 w-8 ${stats.deliveryRate > 95 ? 'text-green-600' : stats.deliveryRate > 90 ? 'text-yellow-600' : 'text-red-600'}`} />
            </div>
            <Progress value={stats.deliveryRate} className="mt-3" />
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Bounce Rate</p>
                <p className="text-2xl font-bold">{stats.bounceRate}%</p>
              </div>
              <XCircle className={`h-8 w-8 ${stats.bounceRate < 2 ? 'text-green-600' : stats.bounceRate < 5 ? 'text-yellow-600' : 'text-red-600'}`} />
            </div>
            <Progress value={stats.bounceRate} className="mt-3 [&>[data-orientation=horizontal]]:bg-red-200" />
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Complaint Rate</p>
                <p className="text-2xl font-bold">{stats.complaintRate}%</p>
              </div>
              <AlertTriangle className={`h-8 w-8 ${stats.complaintRate < 0.1 ? 'text-green-600' : stats.complaintRate < 0.3 ? 'text-yellow-600' : 'text-red-600'}`} />
            </div>
            <Progress value={stats.complaintRate * 10} className="mt-3" />
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm font-medium text-muted-foreground">Reputation Score</p>
                <p className="text-2xl font-bold">{stats.reputationScore}/100</p>
              </div>
              <Shield className={`h-8 w-8 ${stats.reputationScore > 80 ? 'text-green-600' : stats.reputationScore > 60 ? 'text-yellow-600' : 'text-red-600'}`} />
            </div>
            <Progress value={stats.reputationScore} className="mt-3" />
          </CardContent>
        </Card>
      </div>

      {/* Inbox Placement */}
      <Card>
        <CardHeader>
          <CardTitle>Inbox Placement Rate</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-green-600 mb-2">{stats.inboxPlacement}%</div>
              <p className="text-sm text-muted-foreground">Inbox</p>
              <Progress value={stats.inboxPlacement} className="mt-2" />
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-600 mb-2">{stats.spamPlacement}%</div>
              <p className="text-sm text-muted-foreground">Spam/Promotions</p>
              <Progress value={stats.spamPlacement} className="mt-2 [&>[data-orientation=horizontal]]:bg-yellow-200" />
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-red-600 mb-2">{100 - stats.inboxPlacement - stats.spamPlacement}%</div>
              <p className="text-sm text-muted-foreground">Blocked/Missing</p>
              <Progress value={100 - stats.inboxPlacement - stats.spamPlacement} className="mt-2 [&>[data-orientation=horizontal]]:bg-red-200" />
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Server Health */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Server className="h-5 w-5 mr-2" />
            SMTP Server Health
          </CardTitle>
        </CardHeader>
        <CardContent>
          {serverHealth.length === 0 ? (
            <div className="text-center py-8">
              <Server className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No SMTP servers configured</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Server</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>Delivery Rate</TableHead>
                  <TableHead>Bounce Rate</TableHead>
                  <TableHead>Last Check</TableHead>
                  <TableHead>Issues</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {serverHealth.map((server) => (
                  <TableRow key={server.id}>
                    <TableCell className="font-medium">{server.name}</TableCell>
                    <TableCell>
                      <div className="flex items-center">
                        {getStatusIcon(server.status)}
                        <Badge 
                          variant={
                            server.status === 'healthy' ? 'default' :
                            server.status === 'warning' ? 'secondary' : 'destructive'
                          }
                          className="ml-2"
                        >
                          {server.status}
                        </Badge>
                      </div>
                    </TableCell>
                    <TableCell>{server.deliveryRate}%</TableCell>
                    <TableCell>{server.bounceRate}%</TableCell>
                    <TableCell>{new Date(server.lastCheck).toLocaleString()}</TableCell>
                    <TableCell>
                      {server.issues.length === 0 ? (
                        <Badge variant="outline">No issues</Badge>
                      ) : (
                        <Badge variant="destructive">{server.issues.length} issues</Badge>
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Domain Reputation */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Globe className="h-5 w-5 mr-2" />
            Domain Reputation
          </CardTitle>
        </CardHeader>
        <CardContent>
          {domainReputations.length === 0 ? (
            <div className="text-center py-8">
              <Globe className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground">No domains configured</p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Domain</TableHead>
                  <TableHead>Reputation</TableHead>
                  <TableHead>Score</TableHead>
                  <TableHead>Blacklist Status</TableHead>
                  <TableHead>Last Checked</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {domainReputations.map((domain, index) => (
                  <TableRow key={index}>
                    <TableCell className="font-medium">{domain.domain}</TableCell>
                    <TableCell>{getReputationBadge(domain.reputation)}</TableCell>
                    <TableCell>{domain.score}/100</TableCell>
                    <TableCell>
                      {domain.blacklisted ? (
                        <Badge variant="destructive">Blacklisted</Badge>
                      ) : (
                        <Badge variant="default">Clean</Badge>
                      )}
                    </TableCell>
                    <TableCell>{new Date(domain.lastChecked).toLocaleDateString()}</TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* Recommendations */}
      <Card>
        <CardHeader>
          <CardTitle>Deliverability Recommendations</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {stats.bounceRate > 5 && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <strong>High bounce rate detected ({stats.bounceRate}%)</strong><br />
                Consider cleaning your contact list and removing invalid email addresses.
              </AlertDescription>
            </Alert>
          )}

          {stats.complaintRate > 0.3 && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <strong>High complaint rate ({stats.complaintRate}%)</strong><br />
                Review your email content and ensure proper unsubscribe mechanisms.
              </AlertDescription>
            </Alert>
          )}

          {stats.reputationScore < 70 && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <strong>Low sender reputation score ({stats.reputationScore}/100)</strong><br />
                Implement SPF, DKIM, and DMARC authentication for your domains.
              </AlertDescription>
            </Alert>
          )}

          {stats.inboxPlacement < 80 && (
            <Alert>
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                <strong>Low inbox placement rate ({stats.inboxPlacement}%)</strong><br />
                Improve engagement by segmenting your audience and personalizing content.
              </AlertDescription>
            </Alert>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Domains.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { z } from "zod";
import { 
  Globe, 
  Plus, 
  CheckCircle, 
  XCircle, 
  AlertTriangle, 
  Copy, 
  RefreshCw,
  Shield,
  Mail,
  Key
} from "lucide-react";

const domainSchema = z.object({
  domain: z.string().min(1, "Domain is required").regex(/^[a-zA-Z0-9][a-zA-Z0-9-]{1,61}[a-zA-Z0-9]\.[a-zA-Z]{2,}$/, "Invalid domain format"),
});

interface Domain {
  id: string;
  domain: string;
  verified: boolean;
  spfRecord: string;
  spfValid: boolean;
  dkimRecord: string;
  dkimValid: boolean;
  dmarcRecord: string;
  dmarcValid: boolean;
  createdAt: string;
  lastChecked: string;
}

interface DnsRecords {
  spf: string;
  dkim: string;
  dmarc: string;
}

export default function Domains() {
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [selectedDomain, setSelectedDomain] = useState<Domain | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: domains = [], isLoading } = useQuery<Domain[]>({
    queryKey: ["/api/domains"],
  });

  const { data: dnsRecords } = useQuery<DnsRecords>({
    queryKey: ["/api/domains/dns-records", selectedDomain?.id],
    enabled: !!selectedDomain,
  });

  const addDomainMutation = useMutation({
    mutationFn: (data: { domain: string }) => apiRequest("/api/domains", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/domains"] });
      setIsAddDialogOpen(false);
      toast({
        title: "Success",
        description: "Domain added successfully. Please configure DNS records for verification.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const verifyDomainMutation = useMutation({
    mutationFn: (domainId: string) => apiRequest(`/api/domains/${domainId}/verify`, "POST"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/domains"] });
      toast({
        title: "Success",
        description: "Domain verification completed",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Verification Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteDomainMutation = useMutation({
    mutationFn: (domainId: string) => apiRequest(`/api/domains/${domainId}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/domains"] });
      toast({
        title: "Success",
        description: "Domain removed successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const form = useForm<z.infer<typeof domainSchema>>({
    resolver: zodResolver(domainSchema),
    defaultValues: {
      domain: "",
    },
  });

  const onSubmit = (data: z.infer<typeof domainSchema>) => {
    addDomainMutation.mutate(data);
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    toast({
      title: "Copied",
      description: "DNS record copied to clipboard",
    });
  };

  const getVerificationBadge = (isValid: boolean, isVerified: boolean = false) => {
    if (isVerified && isValid) {
      return <Badge variant="default" className="flex items-center"><CheckCircle className="h-3 w-3 mr-1" />Verified</Badge>;
    } else if (isValid) {
      return <Badge variant="secondary" className="flex items-center"><CheckCircle className="h-3 w-3 mr-1" />Valid</Badge>;
    } else {
      return <Badge variant="destructive" className="flex items-center"><XCircle className="h-3 w-3 mr-1" />Invalid</Badge>;
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Domain Authentication</h1>
          <p className="text-muted-foreground mt-1">
            Configure SPF, DKIM, and DMARC records to improve email deliverability.
          </p>
        </div>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Add Domain
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Add Domain</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="domain"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Domain Name</FormLabel>
                      <FormControl>
                        <Input placeholder="example.com" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setIsAddDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={addDomainMutation.isPending}>
                    {addDomainMutation.isPending ? "Adding..." : "Add Domain"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Overview Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Globe className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Total Domains</p>
                <p className="text-2xl font-bold">{domains.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <CheckCircle className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Verified</p>
                <p className="text-2xl font-bold">
                  {domains.filter(d => d.verified).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Shield className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">SPF Configured</p>
                <p className="text-2xl font-bold">
                  {domains.filter(d => d.spfValid).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Key className="h-8 w-8 text-orange-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">DKIM Configured</p>
                <p className="text-2xl font-bold">
                  {domains.filter(d => d.dkimValid).length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Information Alert */}
      <Alert>
        <Mail className="h-4 w-4" />
        <AlertDescription>
          <strong>Email Authentication Setup</strong><br />
          Configure SPF, DKIM, and DMARC records for your domains to improve email deliverability and protect against spoofing. 
          These records help email providers verify that your emails are legitimate.
        </AlertDescription>
      </Alert>

      {/* Domains List */}
      <Card>
        <CardHeader>
          <CardTitle>Your Domains ({domains.length})</CardTitle>
        </CardHeader>
        <CardContent>
          {domains.length === 0 ? (
            <div className="text-center py-8">
              <Globe className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No domains configured</h3>
              <p className="text-muted-foreground mb-4">
                Add your first domain to start configuring email authentication.
              </p>
              <Button onClick={() => setIsAddDialogOpen(true)}>
                <Plus className="h-4 w-4 mr-2" />
                Add Your First Domain
              </Button>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Domain</TableHead>
                  <TableHead>Status</TableHead>
                  <TableHead>SPF</TableHead>
                  <TableHead>DKIM</TableHead>
                  <TableHead>DMARC</TableHead>
                  <TableHead>Last Checked</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {domains.map((domain) => (
                  <TableRow key={domain.id}>
                    <TableCell className="font-medium">{domain.domain}</TableCell>
                    <TableCell>
                      {domain.verified ? (
                        <Badge variant="default" className="flex items-center w-fit">
                          <CheckCircle className="h-3 w-3 mr-1" />
                          Verified
                        </Badge>
                      ) : (
                        <Badge variant="outline" className="flex items-center w-fit">
                          <AlertTriangle className="h-3 w-3 mr-1" />
                          Pending
                        </Badge>
                      )}
                    </TableCell>
                    <TableCell>{getVerificationBadge(domain.spfValid)}</TableCell>
                    <TableCell>{getVerificationBadge(domain.dkimValid)}</TableCell>
                    <TableCell>{getVerificationBadge(domain.dmarcValid)}</TableCell>
                    <TableCell>
                      {domain.lastChecked ? new Date(domain.lastChecked).toLocaleDateString() : "Never"}
                    </TableCell>
                    <TableCell>
                      <div className="flex space-x-2">
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setSelectedDomain(domain)}
                        >
                          Configure
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => verifyDomainMutation.mutate(domain.id)}
                          disabled={verifyDomainMutation.isPending}
                        >
                          <RefreshCw className={`h-4 w-4 ${verifyDomainMutation.isPending ? 'animate-spin' : ''}`} />
                        </Button>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => {
                            if (confirm(`Are you sure you want to remove ${domain.domain}?`)) {
                              deleteDomainMutation.mutate(domain.id);
                            }
                          }}
                        >
                          Remove
                        </Button>
                      </div>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>

      {/* DNS Configuration Modal */}
      {selectedDomain && (
        <Dialog open={!!selectedDomain} onOpenChange={() => setSelectedDomain(null)}>
          <DialogContent className="max-w-4xl">
            <DialogHeader>
              <DialogTitle>Configure DNS for {selectedDomain.domain}</DialogTitle>
            </DialogHeader>

            <Tabs defaultValue="spf" className="w-full">
              <TabsList className="grid w-full grid-cols-3">
                <TabsTrigger value="spf">SPF Record</TabsTrigger>
                <TabsTrigger value="dkim">DKIM Record</TabsTrigger>
                <TabsTrigger value="dmarc">DMARC Record</TabsTrigger>
              </TabsList>

              <TabsContent value="spf" className="space-y-4">
                <div>
                  <h4 className="font-medium mb-2">SPF (Sender Policy Framework)</h4>
                  <p className="text-sm text-muted-foreground mb-4">
                    Add this TXT record to your DNS to specify which servers are allowed to send email for your domain.
                  </p>
                  <div className="bg-muted p-4 rounded-lg">
                    <div className="flex justify-between items-center mb-2">
                      <span className="font-mono text-sm">Type: TXT</span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => copyToClipboard(dnsRecords?.spf || selectedDomain.spfRecord)}
                      >
                        <Copy className="h-4 w-4" />
                      </Button>
                    </div>
                    <div className="font-mono text-sm bg-background p-2 rounded border">
                      {dnsRecords?.spf || selectedDomain.spfRecord || "v=spf1 include:_spf.example.com ~all"}
                    </div>
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="dkim" className="space-y-4">
                <div>
                  <h4 className="font-medium mb-2">DKIM (DomainKeys Identified Mail)</h4>
                  <p className="text-sm text-muted-foreground mb-4">
                    Add this TXT record to enable DKIM signing for your emails.
                  </p>
                  <div className="bg-muted p-4 rounded-lg">
                    <div className="flex justify-between items-center mb-2">
                      <span className="font-mono text-sm">Type: TXT</span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => copyToClipboard(dnsRecords?.dkim || selectedDomain.dkimRecord)}
                      >
                        <Copy className="h-4 w-4" />
                      </Button>
                    </div>
                    <div className="font-mono text-sm bg-background p-2 rounded border break-all">
                      {dnsRecords?.dkim || selectedDomain.dkimRecord || "selector._domainkey.example.com"}
                    </div>
                  </div>
                </div>
              </TabsContent>

              <TabsContent value="dmarc" className="space-y-4">
                <div>
                  <h4 className="font-medium mb-2">DMARC (Domain-based Message Authentication)</h4>
                  <p className="text-sm text-muted-foreground mb-4">
                    Add this TXT record to specify how to handle emails that fail SPF or DKIM checks.
                  </p>
                  <div className="bg-muted p-4 rounded-lg">
                    <div className="flex justify-between items-center mb-2">
                      <span className="font-mono text-sm">Type: TXT</span>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => copyToClipboard(dnsRecords?.dmarc || selectedDomain.dmarcRecord)}
                      >
                        <Copy className="h-4 w-4" />
                      </Button>
                    </div>
                    <div className="font-mono text-sm bg-background p-2 rounded border">
                      {dnsRecords?.dmarc || selectedDomain.dmarcRecord || "v=DMARC1; p=none; rua=mailto:dmarc@example.com"}
                    </div>
                  </div>
                </div>
              </TabsContent>
            </Tabs>

            <div className="flex justify-end space-x-2 pt-4">
              <Button
                variant="outline"
                onClick={() => setSelectedDomain(null)}
              >
                Close
              </Button>
              <Button
                onClick={() => verifyDomainMutation.mutate(selectedDomain.id)}
                disabled={verifyDomainMutation.isPending}
              >
                {verifyDomainMutation.isPending ? "Verifying..." : "Verify DNS Records"}
              </Button>
            </div>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

client/src/pages/ImportContacts.tsx.content:
import { useState, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Upload, Download, FileText, Users, AlertTriangle, CheckCircle, X } from "lucide-react";
import type { ContactList } from "@shared/schema";

interface ImportResult {
  total: number;
  successful: number;
  failed: number;
  duplicates: number;
  errors: string[];
}

export default function ImportContacts() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [selectedListId, setSelectedListId] = useState<string>("");
  const [createNewList, setCreateNewList] = useState(false);
  const [newListName, setNewListName] = useState("");
  const [importProgress, setImportProgress] = useState(0);
  const [importResult, setImportResult] = useState<ImportResult | null>(null);
  const [isImporting, setIsImporting] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: contactLists = [] } = useQuery<ContactList[]>({
    queryKey: ["/api/contact-lists"],
  });

  const importMutation = useMutation({
    mutationFn: async (formData: FormData) => {
      setIsImporting(true);
      setImportProgress(0);

      // Simulate progress updates
      const progressInterval = setInterval(() => {
        setImportProgress(prev => Math.min(prev + 10, 90));
      }, 500);

      try {
        const result = await apiRequest("/api/contacts/import", "POST", formData, {
          headers: {
            // Don't set Content-Type, let the browser set it with boundary for FormData
          },
        });
        clearInterval(progressInterval);
        setImportProgress(100);
        return result;
      } catch (error) {
        clearInterval(progressInterval);
        throw error;
      } finally {
        setIsImporting(false);
      }
    },
    onSuccess: (result: ImportResult) => {
      setImportResult(result);
      queryClient.invalidateQueries({ queryKey: ["/api/contacts"] });
      queryClient.invalidateQueries({ queryKey: ["/api/contact-lists"] });
      toast({
        title: "Import Completed",
        description: `Successfully imported ${result.successful} contacts`,
      });
      // Reset form
      setSelectedFile(null);
      setSelectedListId("");
      setCreateNewList(false);
      setNewListName("");
      if (fileInputRef.current) {
        fileInputRef.current.value = "";
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Import Failed",
        description: error.message,
        variant: "destructive",
      });
      setIsImporting(false);
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (file.type !== "text/csv" && !file.name.endsWith('.csv')) {
        toast({
          title: "Invalid File Type",
          description: "Please select a CSV file",
          variant: "destructive",
        });
        return;
      }
      setSelectedFile(file);
    }
  };

  const handleImport = () => {
    if (!selectedFile) {
      toast({
        title: "No File Selected",
        description: "Please select a CSV file to import",
        variant: "destructive",
      });
      return;
    }

    if (!createNewList && !selectedListId) {
      toast({
        title: "No List Selected",
        description: "Please select a contact list or create a new one",
        variant: "destructive",
      });
      return;
    }

    if (createNewList && !newListName.trim()) {
      toast({
        title: "List Name Required",
        description: "Please enter a name for the new contact list",
        variant: "destructive",
      });
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('listId', selectedListId);
    formData.append('createNewList', createNewList.toString());
    formData.append('newListName', newListName);

    importMutation.mutate(formData);
  };

  const downloadTemplate = () => {
    const csvContent = "email,first_name,last_name,tags\nexample@email.com,John,Doe,newsletter\nexample2@email.com,Jane,Smith,\"newsletter,vip\"";
    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.style.display = 'none';
    a.href = url;
    a.download = 'contact_import_template.csv';
    document.body.appendChild(a);
    a.click();
    window.URL.revokeObjectURL(url);
    document.body.removeChild(a);
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Import Contacts</h1>
        <p className="text-muted-foreground mt-1">
          Upload a CSV file to bulk import your contacts into your lists.
        </p>
      </div>

      {/* Instructions */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <FileText className="h-5 w-5 mr-2" />
            Import Instructions
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div>
              <h4 className="font-medium mb-2">CSV Format Requirements:</h4>
              <ul className="list-disc list-inside space-y-1 text-sm text-muted-foreground">
                <li>Required column: <code className="bg-muted px-1 rounded">email</code></li>
                <li>Optional columns: <code className="bg-muted px-1 rounded">first_name</code>, <code className="bg-muted px-1 rounded">last_name</code>, <code className="bg-muted px-1 rounded">tags</code></li>
                <li>For multiple tags, separate with commas and wrap in quotes: "tag1,tag2,tag3"</li>
                <li>Maximum file size: 10MB</li>
                <li>Maximum contacts per import: 50,000</li>
              </ul>
            </div>
            <Button variant="outline" onClick={downloadTemplate}>
              <Download className="h-4 w-4 mr-2" />
              Download CSV Template
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Import Form */}
      <Card>
        <CardHeader>
          <CardTitle>Upload Contacts</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* File Upload */}
          <div>
            <label className="block text-sm font-medium mb-2">Select CSV File</label>
            <div className="flex items-center space-x-4">
              <Input
                ref={fileInputRef}
                type="file"
                accept=".csv"
                onChange={handleFileSelect}
                className="flex-1"
              />
              {selectedFile && (
                <Badge variant="secondary" className="flex items-center">
                  <FileText className="h-3 w-3 mr-1" />
                  {selectedFile.name}
                </Badge>
              )}
            </div>
          </div>

          {/* List Selection */}
          <div>
            <label className="block text-sm font-medium mb-2">Contact List</label>
            <div className="space-y-3">
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="create-new-list"
                  checked={createNewList}
                  onCheckedChange={(checked) => setCreateNewList(checked === true)}
                />
                <label htmlFor="create-new-list" className="text-sm">
                  Create new contact list
                </label>
              </div>

              {createNewList ? (
                <Input
                  placeholder="Enter new list name"
                  value={newListName}
                  onChange={(e) => setNewListName(e.target.value)}
                />
              ) : (
                <Select value={selectedListId} onValueChange={setSelectedListId}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select existing list" />
                  </SelectTrigger>
                  <SelectContent>
                    {contactLists.map((list) => (
                      <SelectItem key={list.id} value={list.id}>
                        {list.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              )}
            </div>
          </div>

          {/* Import Progress */}
          {isImporting && (
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>Importing contacts...</span>
                <span>{importProgress}%</span>
              </div>
              <Progress value={importProgress} />
            </div>
          )}

          {/* Import Button */}
          <Button
            onClick={handleImport}
            disabled={isImporting || !selectedFile}
            className="w-full"
          >
            {isImporting ? (
              <>
                <Upload className="h-4 w-4 mr-2 animate-spin" />
                Importing...
              </>
            ) : (
              <>
                <Upload className="h-4 w-4 mr-2" />
                Import Contacts
              </>
            )}
          </Button>
        </CardContent>
      </Card>

      {/* Import Results */}
      {importResult && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <CheckCircle className="h-5 w-5 mr-2 text-green-600" />
              Import Results
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
              <div className="text-center">
                <div className="text-2xl font-bold text-blue-600">{importResult.total}</div>
                <div className="text-sm text-muted-foreground">Total Processed</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-green-600">{importResult.successful}</div>
                <div className="text-sm text-muted-foreground">Successful</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-red-600">{importResult.failed}</div>
                <div className="text-sm text-muted-foreground">Failed</div>
              </div>
              <div className="text-center">
                <div className="text-2xl font-bold text-yellow-600">{importResult.duplicates}</div>
                <div className="text-sm text-muted-foreground">Duplicates</div>
              </div>
            </div>

            {importResult.errors.length > 0 && (
              <Alert>
                <AlertTriangle className="h-4 w-4" />
                <AlertDescription>
                  <div className="font-medium mb-2">Import Errors:</div>
                  <ul className="list-disc list-inside space-y-1 text-sm">
                    {importResult.errors.slice(0, 5).map((error, index) => (
                      <li key={index}>{error}</li>
                    ))}
                    {importResult.errors.length > 5 && (
                      <li className="text-muted-foreground">
                        ...and {importResult.errors.length - 5} more errors
                      </li>
                    )}
                  </ul>
                </AlertDescription>
              </Alert>
            )}

            <Button
              variant="outline"
              onClick={() => setImportResult(null)}
              className="mt-4"
            >
              <X className="h-4 w-4 mr-2" />
              Dismiss
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/ListsSegments.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertContactListSchema, type ContactList, type InsertContactList } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Users, Plus, Filter, Edit, Trash2, Mail, TrendingUp } from "lucide-react";

export default function ListsSegments() {
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: contactLists = [], isLoading } = useQuery<ContactList[]>({
    queryKey: ["/api/contact-lists"],
  });

  const { data: contactStats } = useQuery<{
    activeContacts: number;
    segments: number; 
    avgEngagement: string;
  }>({
    queryKey: ["/api/contacts/stats"],
  });

  const createListMutation = useMutation({
    mutationFn: (data: InsertContactList) => apiRequest("/api/contact-lists", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contact-lists"] });
      setIsCreateDialogOpen(false);
      toast({
        title: "Success",
        description: "Contact list created successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const deleteListMutation = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/contact-lists/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/contact-lists"] });
      toast({
        title: "Success",
        description: "Contact list deleted successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const form = useForm<InsertContactList>({
    resolver: zodResolver(insertContactListSchema),
    defaultValues: {
      name: "",
      description: "",
    },
  });

  const onSubmit = (data: InsertContactList) => {
    createListMutation.mutate(data);
  };

  const handleDeleteList = (id: string) => {
    if (confirm("Are you sure you want to delete this list? This action cannot be undone.")) {
      deleteListMutation.mutate(id);
    }
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Lists & Segments</h1>
          <p className="text-muted-foreground mt-1">
            Organize your contacts into targeted lists and segments for better campaign performance.
          </p>
        </div>
        <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Create List
            </Button>
          </DialogTrigger>
          <DialogContent>
            <DialogHeader>
              <DialogTitle>Create Contact List</DialogTitle>
            </DialogHeader>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>List Name</FormLabel>
                      <FormControl>
                        <Input placeholder="e.g., Newsletter Subscribers" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Describe this contact list..."
                          {...field}
                          value={field.value || ""}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex justify-end space-x-2">
                  <Button
                    type="button"
                    variant="outline"
                    onClick={() => setIsCreateDialogOpen(false)}
                  >
                    Cancel
                  </Button>
                  <Button type="submit" disabled={createListMutation.isPending}>
                    {createListMutation.isPending ? "Creating..." : "Create List"}
                  </Button>
                </div>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Overview Stats */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Users className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Total Lists</p>
                <p className="text-2xl font-bold">{contactLists.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Mail className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Active Contacts</p>
                <p className="text-2xl font-bold">{contactStats.activeContacts || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Filter className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Segments</p>
                <p className="text-2xl font-bold">{contactStats.segments || 0}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <TrendingUp className="h-8 w-8 text-orange-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Avg. Engagement</p>
                <p className="text-2xl font-bold">{contactStats.avgEngagement || "0%"}</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Contact Lists */}
      <div className="space-y-4">
        <h2 className="text-xl font-semibold">Your Contact Lists</h2>
        {contactLists.length === 0 ? (
          <Card>
            <CardContent className="p-8 text-center">
              <Users className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No contact lists yet</h3>
              <p className="text-muted-foreground mb-4">
                Create your first contact list to start organizing your subscribers.
              </p>
              <Button onClick={() => setIsCreateDialogOpen(true)}>
                <Plus className="h-4 w-4 mr-2" />
                Create Your First List
              </Button>
            </CardContent>
          </Card>
        ) : (
          <div className="grid gap-4">
            {contactLists.map((list) => (
              <Card key={list.id}>
                <CardHeader>
                  <div className="flex justify-between items-start">
                    <div>
                      <CardTitle className="text-lg">{list.name}</CardTitle>
                      {list.description && (
                        <p className="text-muted-foreground mt-1">{list.description}</p>
                      )}
                    </div>
                    <div className="flex space-x-2">
                      <Button variant="outline" size="sm">
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleDeleteList(list.id)}
                        disabled={deleteListMutation.isPending}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <div className="flex justify-between items-center">
                    <div className="flex space-x-4">
                      <Badge variant="secondary">
                        {/* This will be populated once we add contact counts */}
                        0 contacts
                      </Badge>
                      <Badge variant="outline">
                        Created {list.createdAt ? new Date(list.createdAt).toLocaleDateString() : "Unknown"}
                      </Badge>
                    </div>
                    <div className="flex space-x-2">
                      <Button variant="outline" size="sm">
                        View Contacts
                      </Button>
                      <Button size="sm">
                        Create Campaign
                      </Button>
                    </div>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

