import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import { 
  Plus, 
  Search, 
  Server,
  Activity,
  AlertCircle,
  CheckCircle,
  Edit,
  Trash,
  TestTube,
  Shield,
  Zap
} from "lucide-react";

interface SmtpServer {
  id: string;
  name: string;
  host: string;
  port: number;
  username: string;
  password: string;
  encryption?: string;
  maxEmailsPerHour: number;
  status: 'active' | 'inactive' | 'error';
  createdAt: string;
  updatedAt: string;
}

export default function Servers() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState("");
  const [showAddServer, setShowAddServer] = useState(false);
  const [editingServer, setEditingServer] = useState<SmtpServer | null>(null);

  const [newServer, setNewServer] = useState({
    name: "",
    host: "",
    port: 587,
    username: "",
    password: "",
    encryption: "tls",
    maxEmailsPerHour: 100,
  });

  // Fetch SMTP servers
  const { data: servers, isLoading } = useQuery<SmtpServer[]>({
    queryKey: ['/api/smtp-servers'],
  });

  // Create server mutation
  const createServerMutation = useMutation({
    mutationFn: async (serverData: any) => {
      const response = await apiRequest('POST', '/api/smtp-servers', serverData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/smtp-servers'] });
      setShowAddServer(false);
      setNewServer({
        name: "",
        host: "",
        port: 587,
        username: "",
        password: "",
        encryption: "tls",
        maxEmailsPerHour: 100,
      });
      toast({
        title: "SMTP server added successfully",
        description: "Your SMTP server is ready to use for sending emails.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error adding SMTP server",
        description: "Please check your server configuration and try again.",
        variant: "destructive",
      });
    },
  });

  // Update server mutation
  const updateServerMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string, data: any }) => {
      const response = await apiRequest('PUT', `/api/smtp-servers/${id}`, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/smtp-servers'] });
      setEditingServer(null);
      toast({
        title: "SMTP server updated successfully",
        description: "Your changes have been saved.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error updating SMTP server",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  // Delete server mutation
  const deleteServerMutation = useMutation({
    mutationFn: async (id: string) => {
      const response = await apiRequest('DELETE', `/api/smtp-servers/${id}`, {});
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/smtp-servers'] });
      toast({
        title: "SMTP server deleted successfully",
        description: "The server has been removed from your account.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error deleting SMTP server",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  const handleCreateServer = () => {
    if (!newServer.name || !newServer.host || !newServer.username || !newServer.password) {
      toast({
        title: "Missing required fields",
        description: "Please fill in all required server information.",
        variant: "destructive",
      });
      return;
    }

    createServerMutation.mutate(newServer);
  };

  const handleUpdateServer = () => {
    if (!editingServer) return;

    updateServerMutation.mutate({
      id: editingServer.id,
      data: editingServer,
    });
  };

  const handleDeleteServer = (id: string) => {
    if (confirm("Are you sure you want to delete this SMTP server? This action cannot be undone.")) {
      deleteServerMutation.mutate(id);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'inactive': return 'bg-gray-100 text-gray-800';
      case 'error': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'active': return <CheckCircle className="h-4 w-4" />;
      case 'inactive': return <Activity className="h-4 w-4" />;
      case 'error': return <AlertCircle className="h-4 w-4" />;
      default: return <Activity className="h-4 w-4" />;
    }
  };

  const filteredServers = servers?.filter(server =>
    server.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    server.host.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-slate-900">SMTP Servers</h1>
            <p className="text-slate-600 mt-1">Manage your email delivery servers and configurations.</p>
          </div>
        </div>
        <div className="animate-pulse space-y-4">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-32 bg-slate-200 rounded"></div>
          ))}
        </div>
      </div>
    );
  }

  const activeServers = filteredServers.filter(s => s.status === 'active').length;
  const totalCapacity = filteredServers.reduce((sum, s) => sum + s.maxEmailsPerHour, 0);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-slate-900">SMTP Servers</h1>
          <p className="text-slate-600 mt-1">Manage your email delivery servers and configurations.</p>
        </div>
        <Dialog open={showAddServer} onOpenChange={setShowAddServer}>
          <DialogTrigger asChild>
            <Button className="flex items-center gap-2">
              <Plus className="h-4 w-4" />
              Add Server
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Add SMTP Server</DialogTitle>
            </DialogHeader>
            <div className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="serverName">Server Name*</Label>
                  <Input
                    id="serverName"
                    value={newServer.name}
                    onChange={(e) => setNewServer(prev => ({ ...prev, name: e.target.value }))}
                    placeholder="My SMTP Server"
                  />
                </div>
                <div>
                  <Label htmlFor="serverHost">Host*</Label>
                  <Input
                    id="serverHost"
                    value={newServer.host}
                    onChange={(e) => setNewServer(prev => ({ ...prev, host: e.target.value }))}
                    placeholder="smtp.example.com"
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="serverPort">Port*</Label>
                  <Input
                    id="serverPort"
                    type="number"
                    value={newServer.port}
                    onChange={(e) => setNewServer(prev => ({ ...prev, port: parseInt(e.target.value) || 587 }))}
                    placeholder="587"
                  />
                </div>
                <div>
                  <Label htmlFor="encryption">Encryption</Label>
                  <Select value={newServer.encryption} onValueChange={(value) => setNewServer(prev => ({ ...prev, encryption: value }))}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select encryption" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="none">None</SelectItem>
                      <SelectItem value="ssl">SSL</SelectItem>
                      <SelectItem value="tls">TLS</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="username">Username*</Label>
                  <Input
                    id="username"
                    value={newServer.username}
                    onChange={(e) => setNewServer(prev => ({ ...prev, username: e.target.value }))}
                    placeholder="your-username"
                  />
                </div>
                <div>
                  <Label htmlFor="password">Password*</Label>
                  <Input
                    id="password"
                    type="password"
                    value={newServer.password}
                    onChange={(e) => setNewServer(prev => ({ ...prev, password: e.target.value }))}
                    placeholder="your-password"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="maxEmails">Max Emails per Hour</Label>
                <Input
                  id="maxEmails"
                  type="number"
                  value={newServer.maxEmailsPerHour}
                  onChange={(e) => setNewServer(prev => ({ ...prev, maxEmailsPerHour: parseInt(e.target.value) || 100 }))}
                  placeholder="100"
                />
                <p className="text-sm text-slate-500 mt-1">
                  Set the maximum number of emails this server can send per hour
                </p>
              </div>

              <div className="flex items-center justify-end space-x-3 pt-4 border-t">
                <Button variant="outline" onClick={() => setShowAddServer(false)}>
                  Cancel
                </Button>
                <Button onClick={handleCreateServer} disabled={createServerMutation.isPending}>
                  {createServerMutation.isPending ? 'Adding...' : 'Add Server'}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-600 text-sm mb-1">Total Servers</p>
                <p className="text-2xl font-semibold text-slate-900">{filteredServers.length}</p>
              </div>
              <Server className="h-8 w-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-600 text-sm mb-1">Active Servers</p>
                <p className="text-2xl font-semibold text-slate-900">{activeServers}</p>
              </div>
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-600 text-sm mb-1">Total Capacity</p>
                <p className="text-2xl font-semibold text-slate-900">{totalCapacity.toLocaleString()}/hour</p>
              </div>
              <Zap className="h-8 w-8 text-yellow-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Search */}
      <div className="flex items-center gap-4">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-slate-400" />
          <Input
            placeholder="Search servers..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
          />
        </div>
      </div>

      {/* Servers Grid */}
      {filteredServers.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
          {filteredServers.map((server) => (
            <Card key={server.id} className="hover:shadow-lg transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-lg flex items-center gap-2">
                      {server.name}
                      <Badge className={getStatusColor(server.status)}>
                        {getStatusIcon(server.status)}
                        {server.status}
                      </Badge>
                    </CardTitle>
                    <p className="text-sm text-slate-500 mt-1">{server.host}:{server.port}</p>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="space-y-3">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-slate-500">Username:</span>
                      <div className="font-medium">{server.username}</div>
                    </div>
                    <div>
                      <span className="text-slate-500">Encryption:</span>
                      <div className="font-medium flex items-center gap-1">
                        <Shield className="h-3 w-3" />
                        {server.encryption?.toUpperCase() || 'None'}
                      </div>
                    </div>
                  </div>

                  <div className="text-sm">
                    <span className="text-slate-500">Capacity:</span>
                    <div className="font-medium">{server.maxEmailsPerHour.toLocaleString()} emails/hour</div>
                  </div>

                  <div className="flex items-center justify-between text-sm text-slate-500">
                    <span>Added {new Date(server.createdAt).toLocaleDateString()}</span>
                  </div>

                  <div className="flex items-center gap-2 pt-2 border-t">
                    <Button variant="outline" size="sm" className="flex-1">
                      <TestTube className="h-4 w-4 mr-1" />
                      Test
                    </Button>
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => setEditingServer(server)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button 
                      variant="outline" 
                      size="sm" 
                      className="text-red-600"
                      onClick={() => handleDeleteServer(server.id)}
                    >
                      <Trash className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <Card>
          <CardContent className="text-center py-12">
            <Server className="h-12 w-12 text-slate-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-slate-900 mb-2">No SMTP servers found</h3>
            <p className="text-slate-500 mb-4">
              {searchQuery 
                ? "Try adjusting your search query." 
                : "Add your first SMTP server to start sending emails."}
            </p>
            {!searchQuery && (
              <Button onClick={() => setShowAddServer(true)} className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                Add SMTP Server
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Edit Server Dialog */}
      <Dialog open={!!editingServer} onOpenChange={() => setEditingServer(null)}>
        <DialogContent className="max-w-2xl">
          <DialogHeader>
            <DialogTitle>Edit SMTP Server</DialogTitle>
          </DialogHeader>
          {editingServer && (
            <div className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="editServerName">Server Name*</Label>
                  <Input
                    id="editServerName"
                    value={editingServer.name}
                    onChange={(e) => setEditingServer(prev => prev ? ({ ...prev, name: e.target.value }) : null)}
                    placeholder="My SMTP Server"
                  />
                </div>
                <div>
                  <Label htmlFor="editServerHost">Host*</Label>
                  <Input
                    id="editServerHost"
                    value={editingServer.host}
                    onChange={(e) => setEditingServer(prev => prev ? ({ ...prev, host: e.target.value }) : null)}
                    placeholder="smtp.example.com"
                  />
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="editServerPort">Port*</Label>
                  <Input
                    id="editServerPort"
                    type="number"
                    value={editingServer.port}
                    onChange={(e) => setEditingServer(prev => prev ? ({ ...prev, port: parseInt(e.target.value) || 587 }) : null)}
                    placeholder="587"
                  />
                </div>
                <div>
                  <Label htmlFor="editEncryption">Encryption</Label>
                  <Select 
                    value={editingServer.encryption} 
                    onValueChange={(value) => setEditingServer(prev => prev ? ({ ...prev, encryption: value }) : null)}
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select encryption" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="none">None</SelectItem>
                      <SelectItem value="ssl">SSL</SelectItem>
                      <SelectItem value="tls">TLS</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="editUsername">Username*</Label>
                  <Input
                    id="editUsername"
                    value={editingServer.username}
                    onChange={(e) => setEditingServer(prev => prev ? ({ ...prev, username: e.target.value }) : null)}
                    placeholder="your-username"
                  />
                </div>
                <div>
                  <Label htmlFor="editPassword">Password*</Label>
                  <Input
                    id="editPassword"
                    type="password"
                    value={editingServer.password}
                    onChange={(e) => setEditingServer(prev => prev ? ({ ...prev, password: e.target.value }) : null)}
                    placeholder="your-password"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="editMaxEmails">Max Emails per Hour</Label>
                <Input
                  id="editMaxEmails"
                  type="number"
                  value={editingServer.maxEmailsPerHour}
                  onChange={(e) => setEditingServer(prev => prev ? ({ ...prev, maxEmailsPerHour: parseInt(e.target.value) || 100 }) : null)}
                  placeholder="100"
                />
              </div>

              <div className="flex items-center justify-end space-x-3 pt-4 border-t">
                <Button variant="outline" onClick={() => setEditingServer(null)}>
                  Cancel
                </Button>
                <Button onClick={handleUpdateServer} disabled={updateServerMutation.isPending}>
                  {updateServerMutation.isPending ? 'Updating...' : 'Update Server'}
                </Button>
              </div>
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

client/src/pages/Suppressions.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertSuppressionSchema, type Suppression, type InsertSuppression } from "@shared/schema";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Shield, Plus, Search, Download, Upload, Trash2, AlertTriangle, Ban, Mail } from "lucide-react";

export default function Suppressions() {
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [filterReason, setFilterReason] = useState("all");
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: suppressions = [], isLoading } = useQuery<Suppression[]>({
    queryKey: ["/api/suppressions"],
  });

  const { data: suppressionStats = {} } = useQuery({
    queryKey: ["/api/suppressions/stats"],
  });

  const addSuppressionMutation = useMutation({
    mutationFn: (data: InsertSuppression) => apiRequest("/api/suppressions", "POST", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/suppressions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/suppressions/stats"] });
      setIsAddDialogOpen(false);
      toast({
        title: "Success",
        description: "Email added to suppression list",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const removeSuppressionMutation = useMutation({
    mutationFn: (id: string) => apiRequest(`/api/suppressions/${id}`, "DELETE"),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/suppressions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/suppressions/stats"] });
      toast({
        title: "Success",
        description: "Email removed from suppression list",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const form = useForm<InsertSuppression>({
    resolver: zodResolver(insertSuppressionSchema),
    defaultValues: {
      email: "",
      reason: "unsubscribe",
      source: "manual",
    },
  });

  const onSubmit = (data: InsertSuppression) => {
    addSuppressionMutation.mutate(data);
  };

  const handleRemoveSupression = (id: string) => {
    if (confirm("Are you sure you want to remove this email from the suppression list?")) {
      removeSuppressionMutation.mutate(id);
    }
  };

  const filteredSuppressions = suppressions.filter((suppression) => {
    const matchesSearch = suppression.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesReason = filterReason === "all" || suppression.reason === filterReason;
    return matchesSearch && matchesReason;
  });

  const getReasonBadge = (reason: string) => {
    switch (reason) {
      case "unsubscribe":
        return <Badge variant="secondary">Unsubscribe</Badge>;
      case "bounce":
        return <Badge variant="destructive">Bounce</Badge>;
      case "complaint":
        return <Badge variant="outline">Complaint</Badge>;
      default:
        return <Badge variant="secondary">{reason}</Badge>;
    }
  };

  const exportSuppressions = () => {
    const csvContent = [
      "email,reason,source,created_at",
      ...filteredSuppressions.map(s => 
        `${s.email},${s.reason},${s.source || ''},${s.createdAt}`
      )
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'suppression_list.csv';
    a.click();
    window.URL.revokeObjectURL(url);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold">Suppression List</h1>
          <p className="text-muted-foreground mt-1">
            Manage emails that are blocked from receiving your campaigns.
          </p>
        </div>
        <div className="flex space-x-2">
          <Button variant="outline" onClick={exportSuppressions}>
            <Download className="h-4 w-4 mr-2" />
            Export
          </Button>
          <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="h-4 w-4 mr-2" />
                Add Email
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Add Email to Suppression List</DialogTitle>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email Address</FormLabel>
                        <FormControl>
                          <Input placeholder="user@example.com" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="reason"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Reason</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger>
                              <SelectValue placeholder="Select reason" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            <SelectItem value="unsubscribe">Unsubscribe</SelectItem>
                            <SelectItem value="bounce">Bounce</SelectItem>
                            <SelectItem value="complaint">Complaint</SelectItem>
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="source"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Source</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., manual, campaign_123" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <div className="flex justify-end space-x-2">
                    <Button
                      type="button"
                      variant="outline"
                      onClick={() => setIsAddDialogOpen(false)}
                    >
                      Cancel
                    </Button>
                    <Button type="submit" disabled={addSuppressionMutation.isPending}>
                      {addSuppressionMutation.isPending ? "Adding..." : "Add Email"}
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Stats Overview */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Shield className="h-8 w-8 text-red-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Total Suppressed</p>
                <p className="text-2xl font-bold">{suppressions.length}</p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Ban className="h-8 w-8 text-orange-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Unsubscribes</p>
                <p className="text-2xl font-bold">
                  {suppressions.filter(s => s.reason === 'unsubscribe').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <Mail className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Bounces</p>
                <p className="text-2xl font-bold">
                  {suppressions.filter(s => s.reason === 'bounce').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center">
              <AlertTriangle className="h-8 w-8 text-yellow-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-muted-foreground">Complaints</p>
                <p className="text-2xl font-bold">
                  {suppressions.filter(s => s.reason === 'complaint').length}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Filters */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  placeholder="Search emails..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>
            <Select value={filterReason} onValueChange={setFilterReason}>
              <SelectTrigger className="w-40">
                <SelectValue placeholder="Filter by reason" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">All Reasons</SelectItem>
                <SelectItem value="unsubscribe">Unsubscribe</SelectItem>
                <SelectItem value="bounce">Bounce</SelectItem>
                <SelectItem value="complaint">Complaint</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardContent>
      </Card>

      {/* Suppression List Table */}
      <Card>
        <CardHeader>
          <CardTitle>Suppressed Emails ({filteredSuppressions.length})</CardTitle>
        </CardHeader>
        <CardContent>
          {filteredSuppressions.length === 0 ? (
            <div className="text-center py-8">
              <Shield className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <h3 className="text-lg font-medium mb-2">No suppressed emails</h3>
              <p className="text-muted-foreground">
                {searchTerm || filterReason !== "all" 
                  ? "No emails match your current filters."
                  : "Your suppression list is empty."
                }
              </p>
            </div>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Email</TableHead>
                  <TableHead>Reason</TableHead>
                  <TableHead>Source</TableHead>
                  <TableHead>Date Added</TableHead>
                  <TableHead>Actions</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredSuppressions.map((suppression) => (
                  <TableRow key={suppression.id}>
                    <TableCell className="font-medium">{suppression.email}</TableCell>
                    <TableCell>{getReasonBadge(suppression.reason)}</TableCell>
                    <TableCell>{suppression.source || "â€”"}</TableCell>
                    <TableCell>
                      {new Date(suppression.createdAt).toLocaleDateString()}
                    </TableCell>
                    <TableCell>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleRemoveSupression(suppression.id)}
                        disabled={removeSuppressionMutation.isPending}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Templates.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { isUnauthorizedError } from "@/lib/authUtils";
import { apiRequest } from "@/lib/queryClient";
import EmailTemplateBuilder from "@/components/EmailTemplateBuilder";
import { 
  Plus, 
  Search, 
  FileText,
  Copy,
  Edit,
  Trash,
  Eye,
  Download,
  Star
} from "lucide-react";

interface EmailTemplate {
  id: string;
  name: string;
  subject: string;
  htmlContent: string;
  textContent?: string;
  isDefault: boolean;
  createdAt: string;
  updatedAt: string;
}

export default function Templates() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [searchQuery, setSearchQuery] = useState("");
  const [showCreateTemplate, setShowCreateTemplate] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<EmailTemplate | null>(null);
  const [previewTemplate, setPreviewTemplate] = useState<EmailTemplate | null>(null);

  const [newTemplate, setNewTemplate] = useState({
    name: "",
    subject: "",
    htmlContent: "",
    textContent: "",
    isDefault: false,
  });

  // Fetch templates
  const { data: templates, isLoading } = useQuery<EmailTemplate[]>({
    queryKey: ['/api/templates'],
  });

  // Create template mutation
  const createTemplateMutation = useMutation({
    mutationFn: async (templateData: any) => {
      const response = await apiRequest('POST', '/api/templates', templateData);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/templates'] });
      setShowCreateTemplate(false);
      setNewTemplate({
        name: "",
        subject: "",
        htmlContent: "",
        textContent: "",
        isDefault: false,
      });
      toast({
        title: "Template created successfully",
        description: "Your email template is ready to use.",
      });
    },
    onError: (error) => {
      if (isUnauthorizedError(error)) {
        toast({
          title: "Unauthorized",
          description: "You are logged out. Logging in again...",
          variant: "destructive",
        });
        setTimeout(() => {
          window.location.href = "/api/login";
        }, 500);
        return;
      }
      toast({
        title: "Error creating template",
        description: "Please try again later.",
        variant: "destructive",
      });
    },
  });

  const handleCreateTemplate = () => {
    if (!newTemplate.name || !newTemplate.subject || !newTemplate.htmlContent) {
      toast({
        title: "Missing required fields",
        description: "Please fill in template name, subject, and content.",
        variant: "destructive",
      });
      return;
    }

    createTemplateMutation.mutate(newTemplate);
  };

  const handleDuplicateTemplate = (template: EmailTemplate) => {
    setNewTemplate({
      name: `${template.name} (Copy)`,
      subject: template.subject,
      htmlContent: template.htmlContent,
      textContent: template.textContent || "",
      isDefault: false,
    });
    setShowCreateTemplate(true);
  };

  const filteredTemplates = templates?.filter(template =>
    template.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    template.subject.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-slate-900">Email Templates</h1>
            <p className="text-slate-600 mt-1">Create and manage reusable email templates.</p>
          </div>
        </div>
        <div className="animate-pulse grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[1, 2, 3, 4, 5, 6].map((i) => (
            <div key={i} className="h-48 bg-slate-200 rounded"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-semibold text-slate-900">Email Templates</h1>
          <p className="text-slate-600 mt-1">Create and manage reusable email templates.</p>
        </div>
        <div className="flex space-x-3">
          <Button variant="outline" className="flex items-center gap-2">
            <Download className="h-4 w-4" />
            Export
          </Button>
          <Dialog open={showCreateTemplate} onOpenChange={setShowCreateTemplate}>
            <DialogTrigger asChild>
              <Button className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                New Template
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
              <DialogHeader>
                <DialogTitle>Create Email Template</DialogTitle>
              </DialogHeader>
              <div className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <Label htmlFor="templateName">Template Name*</Label>
                    <Input
                      id="templateName"
                      value={newTemplate.name}
                      onChange={(e) => setNewTemplate(prev => ({ ...prev, name: e.target.value }))}
                      placeholder="Enter template name"
                    />
                  </div>
                  <div>
                    <Label htmlFor="templateSubject">Subject Line*</Label>
                    <Input
                      id="templateSubject"
                      value={newTemplate.subject}
                      onChange={(e) => setNewTemplate(prev => ({ ...prev, subject: e.target.value }))}
                      placeholder="Enter email subject"
                    />
                  </div>
                </div>

                <div>
                  <Label>Email Content*</Label>
                  <div className="mt-2">
                    <EmailTemplateBuilder
                      value={newTemplate.htmlContent}
                      onChange={(html, text) => setNewTemplate(prev => ({ 
                        ...prev, 
                        htmlContent: html,
                        textContent: text || ''
                      }))}
                    />
                  </div>
                </div>

                <div className="flex items-center justify-between pt-4 border-t">
                  <div className="flex items-center space-x-2">
                    <input
                      type="checkbox"
                      id="isDefault"
                      checked={newTemplate.isDefault}
                      onChange={(e) => setNewTemplate(prev => ({ ...prev, isDefault: e.target.checked }))}
                      className="rounded border-slate-300"
                    />
                    <Label htmlFor="isDefault" className="text-sm">Set as default template</Label>
                  </div>
                  <div className="space-x-3">
                    <Button variant="outline" onClick={() => setShowCreateTemplate(false)}>
                      Cancel
                    </Button>
                    <Button onClick={handleCreateTemplate} disabled={createTemplateMutation.isPending}>
                      {createTemplateMutation.isPending ? 'Creating...' : 'Create Template'}
                    </Button>
                  </div>
                </div>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      {/* Search */}
      <div className="flex items-center gap-4">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-slate-400" />
          <Input
            placeholder="Search templates..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
          />
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-600 text-sm mb-1">Total Templates</p>
                <p className="text-2xl font-semibold text-slate-900">{templates?.length || 0}</p>
              </div>
              <FileText className="h-8 w-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-600 text-sm mb-1">Default Templates</p>
                <p className="text-2xl font-semibold text-slate-900">
                  {templates?.filter(t => t.isDefault).length || 0}
                </p>
              </div>
              <Star className="h-8 w-8 text-yellow-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-slate-600 text-sm mb-1">Recently Updated</p>
                <p className="text-2xl font-semibold text-slate-900">
                  {templates?.filter(t => {
                    const daysSinceUpdate = Math.floor((Date.now() - new Date(t.updatedAt).getTime()) / (1000 * 60 * 60 * 24));
                    return daysSinceUpdate <= 7;
                  }).length || 0}
                </p>
              </div>
              <Edit className="h-8 w-8 text-green-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Templates Grid */}
      {filteredTemplates.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {filteredTemplates.map((template) => (
            <Card key={template.id} className="hover:shadow-lg transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <CardTitle className="text-lg flex items-center gap-2">
                      {template.name}
                      {template.isDefault && (
                        <Badge variant="secondary" className="bg-yellow-100 text-yellow-800">
                          Default
                        </Badge>
                      )}
                    </CardTitle>
                    <p className="text-sm text-slate-500 mt-1 line-clamp-2">{template.subject}</p>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="space-y-3">
                  <div className="bg-slate-50 p-3 rounded border min-h-[100px] max-h-[100px] overflow-hidden">
                    <div 
                      className="text-xs text-slate-600 scale-50 origin-top-left w-[200%] h-[200%]"
                      dangerouslySetInnerHTML={{ __html: template.htmlContent }}
                    />
                  </div>

                  <div className="flex items-center justify-between text-sm text-slate-500">
                    <span>Updated {new Date(template.updatedAt).toLocaleDateString()}</span>
                  </div>

                  <div className="flex items-center gap-2">
                    <Button 
                      variant="outline" 
                      size="sm" 
                      className="flex-1"
                      onClick={() => setPreviewTemplate(template)}
                    >
                      <Eye className="h-4 w-4 mr-1" />
                      Preview
                    </Button>
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => handleDuplicateTemplate(template)}
                    >
                      <Copy className="h-4 w-4" />
                    </Button>
                    <Button 
                      variant="outline" 
                      size="sm"
                      onClick={() => setEditingTemplate(template)}
                    >
                      <Edit className="h-4 w-4" />
                    </Button>
                    <Button variant="outline" size="sm" className="text-red-600">
                      <Trash className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <Card>
          <CardContent className="text-center py-12">
            <FileText className="h-12 w-12 text-slate-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-slate-900 mb-2">No templates found</h3>
            <p className="text-slate-500 mb-4">
              {searchQuery 
                ? "Try adjusting your search query." 
                : "Create your first email template to get started."}
            </p>
            {!searchQuery && (
              <Button onClick={() => setShowCreateTemplate(true)} className="flex items-center gap-2">
                <Plus className="h-4 w-4" />
                Create Template
              </Button>
            )}
          </CardContent>
        </Card>
      )}

      {/* Preview Dialog */}
      <Dialog open={!!previewTemplate} onOpenChange={() => setPreviewTemplate(null)}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Template Preview: {previewTemplate?.name}</DialogTitle>
          </DialogHeader>
          {previewTemplate && (
            <div className="space-y-4">
              <div className="border-b pb-4">
                <p className="text-sm text-slate-600">Subject: {previewTemplate.subject}</p>
              </div>
              <div 
                className="border rounded-lg p-4 bg-white min-h-[400px]"
                dangerouslySetInnerHTML={{ __html: previewTemplate.htmlContent }}
              />
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

